import React, { useState } from 'react';
import axios from 'axios';
import RulesService from '../services/RulesService';

const rulesService = new RulesService();
const EvaluateRule = () => {
  const [ruleId, setRuleId] = useState('');
  const [data, setData] = useState('');
  const [message, setMessage] = useState('');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const parsedData = JSON.parse(data); // Assuming input is a valid JSON string
      const response = await rulesService.combineRules(ruleId,parsedData);
      setMessage(response.data.message);
      setResult(response.data.result);
    } catch (error) {
      setMessage(`Error: ${error.response.data.error}`);
    }
  };

  return (
    <div>
      <h2>Evaluate a Rule</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Rule ID:</label>
          <input type="text" value={ruleId} onChange={(e) => setRuleId(e.target.value)} />
        </div>
        <div>
          <label>Data (JSON format):</label>
          <textarea value={data} onChange={(e) => setData(e.target.value)}></textarea>
        </div>
        <button type="submit">Evaluate Rule</button>
      </form>
      {message && <p>{message}</p>}
      {result && <pre>{JSON.stringify(result, null, 2)}</pre>}
    </div>
  );
};

export default EvaluateRule;
