import React, { useState } from 'react';
import RulesService from '../services/RulesService';

const rulesService = new RulesService();

const CreateRule = () => {
    const [ruleName, setRuleName] = useState('');
    const [ruleString, setRuleString] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await rulesService.createRule(ruleName, ruleString);
            setMessage(response.message);
        } catch (error) {
            setMessage(`Error: ${error.message}`);
        }
    };

    return (
        <div>
            <h2>Create a Rule</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Rule Name:</label>
                    <input type="text" value={ruleName} onChange={(e) => setRuleName(e.target.value)} />
                </div>
                <div>
                    <label>Rule String:</label>
                    <textarea value={ruleString} onChange={(e) => setRuleString(e.target.value)}></textarea>
                </div>
                <button type="submit">Create Rule</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default CreateRule;
