import React, { useState } from 'react';
import axios from 'axios';
import RulesService from '../services/RulesService';

const rulesService = new RulesService();
const CombineRules = () => {
  const [ruleIds, setRuleIds] = useState('');
  const [message, setMessage] = useState('');
  const [combinedAST, setCombinedAST] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const ids = ruleIds.split(',').map(id => id.trim());
    try {
      const response = await rulesService.createRule(ruleIds);
      setMessage(response.message);
      setCombinedAST(response.data.ast);
    } catch (error) {
      setMessage(`Error: ${error.response.data.error}`);
    }
  };

  return (
    <div>
      <h2>Combine Rules</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Rule IDs (comma-separated):</label>
          <input type="text" value={ruleIds} onChange={(e) => setRuleIds(e.target.value)} />
        </div>
        <button type="submit">Combine Rules</button>
      </form>
      {message && <p>{message}</p>}
      {combinedAST && <pre>{JSON.stringify(combinedAST, null, 2)}</pre>}
    </div>
  );
};

export default CombineRules;
