import React, { useState } from 'react';
import axios from 'axios';

const CreateRule = () => {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/createRule', {
        rule_name: ruleName,
        rule_string: ruleString,
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(`Error: ${error.response.data.error}`);
    }
  };

  return (
    <div>
      <h2>Create a Rule</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Rule Name:</label>
          <input type="text" value={ruleName} onChange={(e) => setRuleName(e.target.value)} />
        </div>
        <div>
          <label>Rule String:</label>
          <textarea value={ruleString} onChange={(e) => setRuleString(e.target.value)}></textarea>
        </div>
        <button type="submit">Create Rule</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreateRule;
