// src/services/AxiosService.js
import axios from 'axios';
const config = require('../configrations/Configration');

export default class AxiosService {
    constructor() {
        this.createApi = axios.create({
            baseURL: config.createURL, // Base URL for create operations
            headers: {
                'Content-Type': 'application/json',
            },
        });

        this.combineApi = axios.create({
            baseURL: config.combineURL, // Base URL for combine operations
            headers: {
                'Content-Type': 'application/json',
            },
        });

        this.evaluateApi = axios.create({
            baseURL: config.evaluateURL, // Base URL for evaluate operations
            headers: {
                'Content-Type': 'application/json',
            },
        });
    }

    async postCreate(data) {
        try {
            const response = await this.createApi.post('/create_rule', data);
            return response.data;
        } catch (error) {
            throw new Error(`POST request failed: ${error.message}`);
        }
    }

    async postCombine(data) {
        try {
            const response = await this.combineApi.post('/combineRules', data);
            return response.data;
        } catch (error) {
            throw new Error(`POST request failed: ${error.message}`);
        }
    }

    async postEvaluate(data) {
        try {
            const response = await this.evaluateApi.post('/evaluateRule', data);
            return response.data;
        } catch (error) {
            throw new Error(`POST request failed: ${error.message}`);
        }
    }
}
