// src/services/RulesService.js
import AxiosService from './AxiosService';

const axiosService = new AxiosService();

export default class RulesService {
    // Method to create a new rule
    async createRule(ruleName, ruleString) {
        const response = await axiosService.post('/api/createRule', {
            rule_name: ruleName,
            rule_string: ruleString,
        });
        return response.data;
    }

    // Method to combine rules
    async combineRules(ruleIds) {
        const response = await axiosService.post('/api/combineRules', {
            rule_ids: ruleIds,
        });
        return response.data;
    }

    // Method to evaluate a rule
    async evaluateRule(ruleId, data) {
        const response = await axiosService.post('/api/evaluateRule', {
            rule_id: ruleId,
            data,
        });
        return response.data;
    }
}
